import { Component } from '@angular/core';

@Component({
  selector: 'app-if-else-com',
  templateUrl: './if-else-com.component.html',
  styleUrls: ['./if-else-com.component.css']
})
export class IfElseComComponent {
// show=true

show='yes'
}
<!-- <p>if-else-com works!</p> -->

<h1 *ngIf="show=='yes', then ifBlock else elseBlock">
NPST
</h1>

<ng-template #ifBlock>
<h1>if Condition</h1>
</ng-template>
<ng-template #elseBlock>
<h1>Else Condition</h1>
</ng-template>
