  selector: 'app-pipes-com',
  templateUrl: './pipes-com.component.html',
  styleUrls: ['./pipes-com.component.css']
})
export class PipesComComponent {
  title:string="Angular Basic Pipes"

  today=Date();
}
<p>pipes-com works!</p>

<h1>{{title | uppercase}}</h1>
<h1>{{title | lowercase}}</h1>
