import { Component } from '@angular/core';

@Component({
  selector: 'app-switch-case-com',
  templateUrl: './switch-case-com.component.html',
  styleUrls: ['./switch-case-com.component.css']
})
export class SwitchCaseComComponent {
color=''
}
<!-- <p>switch-case-com works!</p> -->
<div [ngSwitch]="color">
    <h1 *ngSwitchCase="'red'" style="color:red">Red color</h1>
    <h1 *ngSwitchCase="'green'" style="color:green">Green color</h1>
    <h1 *ngSwitchCase="'blue'" style="color:blue">blue color</h1>
    <h1 *ngSwitchCase="'black'" style="color:black">black color</h1>
    <h1 *ngSwitchCase="'white'" style="color:white">white color</h1>
    <h1 *ngSwitchDefault="">XYZ color</h1>
</div>
