import { Component } from '@angular/core';

@Component({
  selector: 'app-else-id-com',
  templateUrl: './else-id-com.component.html',
  styleUrls: ['./else-id-com.component.css']
})
export class ElseIdComComponent {
color='green'
}
<!-- <p>else-id-com works!</p> -->
<ng-template [ngIf]="color==='red' ">
    <h1 style="color:red">
        red color
    </h1>
</ng-template>

<ng-template [ngIf]="color==='green' ">
    <h1 style="color:green">
        green color
    </h1>
</ng-template>
